package com.example.zhubingning.kotlinarchitecturetestimport android.app.Applicationimport com.example.zhubingning.kotlinarchitecturetest.di.component.ApplicationComponentimport com.example.zhubingning.kotlinarchitecturetest.di.component.DaggerApplicationComponentimport com.example.zhubingning.kotlinarchitecturetest.di.module.ApplicationModuleclass BaseApplication: Application() {	lateinit var component: ApplicationComponent	override fun onCreate() {		super.onCreate()		instance = this		setup()		if (BuildConfig.DEBUG) {			// Maybe TimberPlant etc.		}	}	private fun setup() {		// todo add some inject objects such as crashlytics / stecho		component = DaggerApplicationComponent.builder()				.applicationModule(ApplicationModule(this)).build()		component.inject(this)	}	fun getApplicationComponent(): ApplicationComponent {		return component	}	companion object {		lateinit var instance: BaseApplication private set	}}