package com.example.zhubingning.kotlinarchitecturetest.di.moduleimport android.app.Applicationimport com.example.zhubingning.kotlinarchitecturetest.BaseApplicationimport com.example.zhubingning.kotlinarchitecturetest.di.scope.PerApplicationimport dagger.Moduleimport dagger.Providesimport javax.inject.Singleton@Moduleclass ApplicationModule(private val baseApp: BaseApplication) {	@Provides	@Singleton	@PerApplication	fun provideApplication(): Application {		return baseApp	}}